mapper.select:
SELECT MTD.PO_CD "원자재발주코드", MTD.MT_CD "원자재코드", M.MT_NM "원자재명", V.VDR_NM "업체명", BIM.B_IN_QTY "입고량", TO_CHAR(MTD.CHK_DT, 'YYYY-MM-DD') "입고일시", BIM.mt_lot "원자재LOT번호", TO_CHAR(MTD.CHK_DT+31 ,'YYYY-MM-DD') "유통기한"
FROM MT_CHK_DTL MTD, MT M, VENDOR V, BEFOR_IN_MT BIM, MT_LOT lot
WHERE MTD.MT_CD = M.MT_CD AND M.VDR_CD = V.VDR_CD AND MTD.CHK_CD = BIM.CHK_CD AND lot.mt_cd = m.mt_cd AND MTD.CHK_DTL_INSP = 'MTRL02' AND m.mt_cd = #{원자재코드}

mapper.update:
UPDATE mt
SET    mt_sfqty = #{safe}
WHERE  mt_nm    = #{mtNm}
